'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const hasOwnProperty = generic.function({name: 'hasOwnProperty', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-object.prototype.hasownproperty
hasOwnProperty.method([_, is(Object)],
  (V, object) =>
    object.hasOwnProperty(V)
)

module.exports = hasOwnProperty
