'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const includes = generic.function({name: 'includes', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-string.prototype.includes
includes.method([_, _, is(String)],
  (searchString, position, string) =>
    string.includes(searchString, position)
)

module.exports = includes
