'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const isPrototypeOf = generic.function({name: 'isPrototypeOf', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-object.prototype.isprototypeof
isPrototypeOf.method([_, is(Object)],
  (V, object) =>
    object.isPrototypeOf(V)
)

module.exports = isPrototypeOf
