'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const join = generic.function({name: 'join', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-array.prototype.join
join.method([_, is(Array)],
  (separator, array) =>
    array.join(separator)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.join
join.method([_, is(Int8Array)],
  (separator, int8Array) =>
    int8Array.join(separator)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.join
join.method([_, is(Uint8Array)],
  (separator, uint8Array) =>
    uint8Array.join(separator)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.join
join.method([_, is(Uint8ClampedArray)],
  (separator, uint8ClampedArray) =>
    uint8ClampedArray.join(separator)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.join
join.method([_, is(Int16Array)],
  (separator, int16Array) =>
    int16Array.join(separator)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.join
join.method([_, is(Uint16Array)],
  (separator, uint16Array) =>
    uint16Array.join(separator)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.join
join.method([_, is(Int32Array)],
  (separator, int32Array) =>
    int32Array.join(separator)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.join
join.method([_, is(Uint32Array)],
  (separator, uint32Array) =>
    uint32Array.join(separator)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.join
join.method([_, is(Float32Array)],
  (separator, float32Array) =>
    float32Array.join(separator)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.join
join.method([_, is(Float64Array)],
  (separator, float64Array) =>
    float64Array.join(separator)
)

module.exports = join
