'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const lastIndexOf = generic.function({name: 'lastIndexOf', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-string.prototype.lastindexof
lastIndexOf.method([_, _, is(String)],
  (searchString, position, string) =>
    string.lastIndexOf(searchString, position)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-array.prototype.lastindexof
lastIndexOf.method([_, _, is(Array)],
  (searchElement, fromIndex, array) =>
    array.lastIndexOf(searchElement, fromIndex)
)

module.exports = lastIndexOf
