'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const link = generic.function({name: 'link', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-string.prototype.link
link.method([_, is(String)],
  (url, string) =>
    string.link(url)
)

module.exports = link
