'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const match = generic.function({name: 'match', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-string.prototype.match
match.method([_, is(String)],
  (regexp, string) =>
    string.match(regexp)
)

module.exports = match
