'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const normalize = generic.function({name: 'normalize', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-string.prototype.normalize
normalize.method([_, is(String)],
  (form, string) =>
    string.normalize(form)
)

module.exports = normalize
