'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const pop = generic.function({name: 'pop', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-array.prototype.pop
pop.method([is(Array)],
  (array) =>
    array.pop()
)

module.exports = pop
