'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const propertyIsEnumerable = generic.function({name: 'propertyIsEnumerable', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-object.prototype.propertyisenumerable
propertyIsEnumerable.method([_, is(Object)],
  (V, object) =>
    object.propertyIsEnumerable(V)
)

module.exports = propertyIsEnumerable
