'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const push = generic.function({name: 'push', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-array.prototype.push
push.method([_, is(Array)],
  (items, array) =>
    array.push(items)
)

module.exports = push
