'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const reduceRight = generic.function({name: 'reduceRight', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-array.prototype.reduceright
reduceRight.method([_, _, is(Array)],
  (callbackfn, initialValue, array) =>
    array.reduceRight(callbackfn, initialValue)
)

module.exports = reduceRight
