'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const repeat = generic.function({name: 'repeat', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-string.prototype.repeat
repeat.method([_, is(String)],
  (count, string) =>
    string.repeat(count)
)

module.exports = repeat
