'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const replace = generic.function({name: 'replace', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-string.prototype.replace
replace.method([_, _, is(String)],
  (searchValue, replaceValue, string) =>
    string.replace(searchValue, replaceValue)
)

module.exports = replace
