'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const search = generic.function({name: 'search', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-string.prototype.search
search.method([_, is(String)],
  (regexp, string) =>
    string.search(regexp)
)

module.exports = search
