'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const setDate = generic.function({name: 'setDate', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.setdate
setDate.method([_, is(Date)],
  (day, date) =>
    date.setDate(day)
)

module.exports = setDate
