'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const setFullYear = generic.function({name: 'setFullYear', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.setfullyear
setFullYear.method([_, _, _, is(Date)],
  (year, month, day, date) =>
    date.setFullYear(year, month, day)
)

module.exports = setFullYear
