'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const setHours = generic.function({name: 'setHours', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.sethours
setHours.method([_, _, _, _, is(Date)],
  (hour, min, sec, ms, date) =>
    date.setHours(hour, min, sec, ms)
)

module.exports = setHours
