'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const setInt8 = generic.function({name: 'setInt8', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-dataview.prototype.setint8
setInt8.method([_, _, is(DataView)],
  (byteOffset, value, dataView) =>
    dataView.setInt8(byteOffset, value)
)

module.exports = setInt8
