'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const setMilliseconds = generic.function({name: 'setMilliseconds', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.setmilliseconds
setMilliseconds.method([_, is(Date)],
  (ms, date) =>
    date.setMilliseconds(ms)
)

module.exports = setMilliseconds
