'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const setMonth = generic.function({name: 'setMonth', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.setmonth
setMonth.method([_, _, is(Date)],
  (month, day, date) =>
    date.setMonth(month, day)
)

module.exports = setMonth
