'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const setSeconds = generic.function({name: 'setSeconds', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.setseconds
setSeconds.method([_, _, is(Date)],
  (sec, ms, date) =>
    date.setSeconds(sec, ms)
)

module.exports = setSeconds
