'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const setTime = generic.function({name: 'setTime', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.settime
setTime.method([_, is(Date)],
  (time, date) =>
    date.setTime(time)
)

module.exports = setTime
