'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const setUTCDate = generic.function({name: 'setUTCDate', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.setutcdate
setUTCDate.method([_, is(Date)],
  (day, date) =>
    date.setUTCDate(day)
)

module.exports = setUTCDate
