'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const setUTCFullYear = generic.function({name: 'setUTCFullYear', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.setutcfullyear
setUTCFullYear.method([_, _, _, is(Date)],
  (year, month, day, date) =>
    date.setUTCFullYear(year, month, day)
)

module.exports = setUTCFullYear
