'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const setUTCMilliseconds = generic.function({name: 'setUTCMilliseconds', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.setutcmilliseconds
setUTCMilliseconds.method([_, is(Date)],
  (ms, date) =>
    date.setUTCMilliseconds(ms)
)

module.exports = setUTCMilliseconds
