'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const setUTCMinutes = generic.function({name: 'setUTCMinutes', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.setutcminutes
setUTCMinutes.method([_, _, _, is(Date)],
  (min, sec, ms, date) =>
    date.setUTCMinutes(min, sec, ms)
)

module.exports = setUTCMinutes
