'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const setUTCMonth = generic.function({name: 'setUTCMonth', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.setutcmonth
setUTCMonth.method([_, _, is(Date)],
  (month, day, date) =>
    date.setUTCMonth(month, day)
)

module.exports = setUTCMonth
