'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const setUTCSeconds = generic.function({name: 'setUTCSeconds', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.setutcseconds
setUTCSeconds.method([_, _, is(Date)],
  (sec, ms, date) =>
    date.setUTCSeconds(sec, ms)
)

module.exports = setUTCSeconds
