'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const setUint8 = generic.function({name: 'setUint8', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-dataview.prototype.setuint8
setUint8.method([_, _, is(DataView)],
  (byteOffset, value, dataView) =>
    dataView.setUint8(byteOffset, value)
)

module.exports = setUint8
