'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const setYear = generic.function({name: 'setYear', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.setyear
setYear.method([_, is(Date)],
  (year, date) =>
    date.setYear(year)
)

module.exports = setYear
