'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const shift = generic.function({name: 'shift', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-array.prototype.shift
shift.method([is(Array)],
  (array) =>
    array.shift()
)

module.exports = shift
