// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.some
some.method([_, is(Int16Array)],
  (callbackfn, int16Array) =>
    int16Array.some(callbackfn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.some
