'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const sort = generic.function({name: 'sort', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-array.prototype.sort
sort.method([_, is(Array)],
  (comparefn, array) =>
    array.sort(comparefn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.sort
sort.method([_, is(Int8Array)],
  (comparefn, int8Array) =>
    int8Array.sort(comparefn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.sort
sort.method([_, is(Uint8Array)],
  (comparefn, uint8Array) =>
    uint8Array.sort(comparefn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.sort
sort.method([_, is(Uint8ClampedArray)],
  (comparefn, uint8ClampedArray) =>
    uint8ClampedArray.sort(comparefn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.sort
sort.method([_, is(Int16Array)],
  (comparefn, int16Array) =>
    int16Array.sort(comparefn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.sort
sort.method([_, is(Uint16Array)],
  (comparefn, uint16Array) =>
    uint16Array.sort(comparefn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.sort
sort.method([_, is(Int32Array)],
  (comparefn, int32Array) =>
    int32Array.sort(comparefn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.sort
sort.method([_, is(Uint32Array)],
  (comparefn, uint32Array) =>
    uint32Array.sort(comparefn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.sort
sort.method([_, is(Float32Array)],
  (comparefn, float32Array) =>
    float32Array.sort(comparefn)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.sort
sort.method([_, is(Float64Array)],
  (comparefn, float64Array) =>
    float64Array.sort(comparefn)
)

module.exports = sort
