'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const splice = generic.function({name: 'splice', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-array.prototype.splice
splice.method([_, _, _, is(Array)],
  (start, deleteCount, items, array) =>
    array.splice(start, deleteCount, items)
)

module.exports = splice
