'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const split = generic.function({name: 'split', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-string.prototype.split
split.method([_, _, is(String)],
  (separator, limit, string) =>
    string.split(separator, limit)
)

module.exports = split
