'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const startsWith = generic.function({name: 'startsWith', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-string.prototype.startswith
startsWith.method([_, _, is(String)],
  (searchString, position, string) =>
    string.startsWith(searchString, position)
)

module.exports = startsWith
