'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._

const stringify = generic.function({name: 'stringify', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-json.stringify
stringify.method([_, _, _],
  (replacer, space, value) =>
    JSON.stringify(value, replacer, space)
)

module.exports = stringify
