'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const substr = generic.function({name: 'substr', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-string.prototype.substr
substr.method([_, _, is(String)],
  (start, length, string) =>
    string.substr(start, length)
)

module.exports = substr
