'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const substring = generic.function({name: 'substring', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-string.prototype.substring
substring.method([_, _, is(String)],
  (start, end, string) =>
    string.substring(start, end)
)

module.exports = substring
