'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const test = generic.function({name: 'test', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-regexp.prototype.test
test.method([_, is(RegExp)],
  (S, regExp) =>
    regExp.test(S)
)

module.exports = test
