'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const then = generic.function({name: 'then', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-promise.prototype.then
then.method([_, _, is(Promise)],
  (onFulfilled, onRejected, promise) =>
    promise.then(onFulfilled, onRejected)
)

module.exports = then
