'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const toDateString = generic.function({name: 'toDateString', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.todatestring
toDateString.method([is(Date)],
  (date) =>
    date.toDateString()
)

module.exports = toDateString
