'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const toExponential = generic.function({name: 'toExponential', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-number.prototype.toexponential
toExponential.method([_, is(Number)],
  (fractionDigits, number) =>
    number.toExponential(fractionDigits)
)

module.exports = toExponential
