'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const toFixed = generic.function({name: 'toFixed', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-number.prototype.tofixed
toFixed.method([_, is(Number)],
  (fractionDigits, number) =>
    number.toFixed(fractionDigits)
)

module.exports = toFixed
