'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const toGMTString = generic.function({name: 'toGMTString', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.togmtstring
toGMTString.method([is(Date)],
  (date) =>
    date.toGMTString()
)

module.exports = toGMTString
