'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const toISOString = generic.function({name: 'toISOString', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.toisostring
toISOString.method([is(Date)],
  (date) =>
    date.toISOString()
)

module.exports = toISOString
