'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const toJSON = generic.function({name: 'toJSON', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.tojson
toJSON.method([_, is(Date)],
  (key, date) =>
    date.toJSON(key)
)

module.exports = toJSON
