'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const toLocaleString = generic.function({name: 'toLocaleString', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-number.prototype.tolocalestring
toLocaleString.method([_, _, is(Number)],
  (reserved1, reserved2, number) =>
    number.toLocaleString(reserved1, reserved2)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.tolocalestring
toLocaleString.method([_, _, is(Date)],
  (reserved1, reserved2, date) =>
    date.toLocaleString(reserved1, reserved2)
)

module.exports = toLocaleString
