'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const toLowerCase = generic.function({name: 'toLowerCase', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-string.prototype.tolowercase
toLowerCase.method([is(String)],
  (string) =>
    string.toLowerCase()
)

module.exports = toLowerCase
